if ( 0 < Session.g_strPassword.length)
{
	Server.logInfo("password length > 0");
	if( 0 < Session.g_strPassword.indexOf('#') || 
		0 < Session.g_strPassword.indexOf('t') ) {
			Server.logInfo("found # or t");
			Session.g_strPassword = Session.g_strPassword.substring(0,Session.g_strPassword.length - 1);
	}
}
