Server.logInfo("Received a " + Session.oSipMsg.method ) ;
js_disable_event();
Session.oSipMsg.LogInfo();
Session.strStatus = Session.oSipMsg.status;

var name, id, dialog, idx ;
if( Session.oSipMsg.hdr.content.length > 0 ) {
	Server.logInfo("CONTENT LENGTH > 0") ;
	var s = new String( Session.oSipMsg.hdr.content ) ;
	if( parse_xml_tag_attribute( s, "event", "name", name ) ) {
		Server.logInfo("GOT EVENT NAME") ;
		/* parse connection id */
		parse_xml_tag_attribute( s, "event", "id", id ) ;

		idx = id.indexOf("dialog:") ;
		if( -1 != idx ) {
			Server.logInfo("IDX FOUND DIALOG:") ;
			dialog = id.substr( idx + 7 ) ;
		}
		if( name == "msml.dialog.exit" ) {
			Server.logInfo("NAME IS MSML.DIALOG.EXIT") ;
			if( dialog == Session.g_epMain._media_dialog_id ) {
				Server.logInfo("Received a dialog exit for current dialog id: " + dialog ) ;
				Session.g_epMain.nPlayOffset = 0 ;
				Session.g_epMain.strFilename = "" ;
				Session.g_epMain._media_dialog_id = 0 ;
				Session.bPlayCompleted = true ;
			}				
			else {
				idx = s.indexOf("<name>dtmf.digits</name>") ;
				if( -1 != idx ) {
					Server.logInfo("IDX IS DTMF.DIGITS") ;
					/* we have a digit report */
					s = s.substr( idx ) ;
					if( parse_xml_tag_value( s, "value", Session.strDtmf ) ) {
						if( Session.strDtmf.length > 0 ) {
							Session.bDigitsPressed = true ;
							Server.logInfo("Host entered dtmf : " + Session.strDtmf ) ;
						}
						else {
							Session.bDigitsPressed = false ;
							Server.logInfo("No digit pressed") ;
						}
					}
				}
				idx = s.indexOf("<name>dialog.exit.status</name>")
				if (-1 != idx )
				{
					Server.logInfo("IDX IS DIALOG.EXIT.STATUS") ;
					s = s.substr( idx );
					if( parse_xml_tag_value( s, "value", Session.strStatus ) ) {
						if( Session.strStatus == "400" ) {
							Session.bFail = true ;
							Server.logInfo("TURN ON DTMF, GET FAILURE RESPONSE") ;
						}
						else {
							Session.bFail = false ;
							Server.logInfo("TURN ON DTMF, GET SUCCESS RESPONSE") ;
						}
					}		
				}
			}
		}
		else if( name == "app.playdone" && dialog == Session.g_epPlay._media_dialog_id ) {
 				Server.logInfo("Received a play done for current dialog id: " + dialog ) ;
				Session.g_epMain.nPlayOffset = 0 ;
				Session.g_epMain.strFilename = "" ;
